definition.script:
pipeline {
    agent {
        docker { 
            image 'reshnm/dacapo' 
            label 'linux_x86_64'
            reuseNode true
        }
    }
    stages {
        stage('Clone SapMachine Infrastructure') {
            steps {
                git 'https://github.com/SAP/SapMachine-infrastructure.git'
                sh "ls -l"
            }
        }
        stage('Run Dacapo') {
            steps {
                sh "java -version"
                sh "which java"
                sh "python --version"
                sh "ls -l"
                sh "ls -l /opt"
                sh "ls -l /opt/sapmachine-11-jdk"
                sh "python lib/run_dacapo.py '/opt/dacapo/dacapo.jar'"
            }
        }
    }
}
