definition.script:
pipeline {
    agent {

        label "macos_x86_64"

    }
    stages {
        stage("Build") {
            environment {
                SAPMACHINE_PUBLISH_GITHUB_TOKEN = credentials('SapMachine-Github-Token')
                SAPMACHINE_GIT_USER = 'SapMachine'
                SAPMACHINE_GIT_EMAIL = 'sapmachine@sap.com'
                CREATE_NO_BUILD_TAR_GZ = 'true'
            }
            steps {
                sh "ls -la"
                checkout changelog: false, poll: false, scm: [$class: 'GitSCM', branches: [[name: '*/master']], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'RelativeTargetDirectory', relativeTargetDir: 'SapMachine-Infrastructure']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'SapMachine-github', url: 'https://github.com/SAP/SapMachine-infrastructure.git']]]
                checkout([$class: 'GitSCM', branches: [[name: 'refs/heads/sapmachine11']], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'RelativeTargetDirectory', relativeTargetDir: 'SapMachine']], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'SapMachine-github', url: 'https://github.com/SAP/SapMachine.git']]])
                withCredentials([
                        [$class: 'UsernamePasswordMultiBinding', credentialsId: 'SapMachine-github', usernameVariable: 'GIT_USER', passwordVariable: 'GIT_PASSWORD']
                        ]) {
                    sh '''
                        SapMachine-Infrastructure/build-pipe/build.sh
                        '''
                    }
            }
            post {
                success {
                    junit 'gtest.xml'
                }
            }
        }
    }
    post {
        always {
            cleanWs deleteDirs: true, disableDeferredWipeout: true
        }
    }
}
